import { useState } from "react";
import "./App.css"; // Import the CSS file here

const api = {
  key: "d480b58953018721d64aaf9795fcb96a",
  base: "https://api.openweathermap.org/data/2.5/",
};

function App() {
  const [search, setSearch] = useState("");
  const [weather, setWeather] = useState({});

  const searchClicked = () => {
    fetch(`${api.base}weather?q=${search}&units=metric&APPID=${api.key}`)
      .then((res) => res.json())
      .then((result) => {
        setWeather(result);
      });
  };

  return (
    <div className="min-h-screen bg-image flex flex-col items-center justify-center bg-opacity-50">
     <nav class="bg-blue-500 p-4">
  <div class="flex items-center justify-between">
    {/* <!-- Logo Image --> */}
    <img src="your-logo-image.jpg" alt="Logo" class="h-8 w-8 mr-2" />

    {/* <!-- App Name --> */}
    <h1 class="text-white text-lg font-semibold">WeatherApp</h1>

    {/* <!-- Navigation Links (if needed) --> */}
    <ul class="flex space-x-4">
      <li><a href="#" class="text-white hover:text-gray-300">Home</a></li>
      <li><a href="#" class="text-white hover:text-gray-300">About</a></li>
      <li><a href="#" class="text-white hover:text-gray-300">Contact</a></li>
    </ul>
  </div>
</nav>

      {/* HEADER */}
      <h1 className="text-4xl font-bold mb-4">Weather App</h1>

      {/* SEARCH BOX */}
      <div className="flex items-center space-x-4">
        <input
          type="text"
          placeholder="Enter City"
          className="px-4 py-2 border rounded-lg"
          onChange={(event) => setSearch(event.target.value)}
        />
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg"
          onClick={searchClicked}
        >
          Search
        </button>
      </div>

      {/* WEATHER INFO */}
      <div className="mt-8 text-center">
        {weather.name && (
          <p className="text-2xl font-semibold">{weather.name}</p>
        )}
        {weather.main && (
          <p className="text-4xl font-extrabold mt-2">{weather.main.temp}Â°C</p>
        )}
        {weather.weather && weather.weather[0] && (
          <p className="text-lg mt-2">{weather.weather[0].description}</p>
        )}
      </div>
    </div>
  );
}

export default App;
