import { useState } from "react";
import logo from "./images/logo.png";
import "./App.css";

const api = {
  key: "d480b58953018721d64aaf9795fcb96a",
  base: "https://api.openweathermap.org/data/2.5/",
};

function App() {
  const [search, setSearch] = useState("");
  const [weather, setWeather] = useState({});

  const searchClicked = () => {
    fetch(`${api.base}weather?q=${search}&units=metric&APPID=${api.key}`)
      .then((res) => res.json())
      .then((result) => {
        setWeather(result);
      });
  };

  return (
    <>
      {/* <nav class="bg-gradient-to-r from-blue-200 via-gray-300 to-yellow-200 p-4"> */}
      {/* <nav class="bg-gradient-to-r from-yellow-200 via-green-200 to-blue-200 p-4"> */}
      {/* <nav class="bg-gradient-to-r from-blue-200 via-yellow-200 to-gray-200 p-4"> */}
      {/* <nav class="bg-gradient-to-r from-green-200 via-gray-200 to-yellow-200 p-4"> */}
      {/* <nav class="bg-gradient-to-r from-gray-200 via-yellow-200 to-blue-200 p-4"> */}
      <nav class="bg-gradient-to-r from-white via-yellow-200 to-blue-200 p-4">
        <div class="flex items-center justify-between">
          {/* <!-- Logo Image --> */}
          <img src={logo} alt="Logo" class="h-12 w-20 mr-2" />

          {/* <!-- App Name --> */}
          {/* <h1 class="text-blue text-lg font-semibold">WeatherWhiz</h1> */}
          <h1 class="text-blue-500 text-lg font-semibold italic">WeatherWhiz</h1>

        </div>
      </nav>

      <div className="min-h-screen bg-image flex flex-col items-center justify-center bg-opacity-50">
        {/* SEARCH BOX */}
        <div className="flex items-center space-x-4">
          <input
            type="text"
            placeholder="Enter City"
            className="px-4 py-2 border rounded-lg"
            onChange={(event) => setSearch(event.target.value)}
          />
          <button
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg"
            onClick={searchClicked}
          >
            Search
          </button>
        </div>

        {/* WEATHER INFO */}
        <div className="mt-8 text-center">
          {weather.name && (
            <p className="text-2xl font-semibold">{weather.name}</p>
          )}
          {weather.main && (
            <p className="text-4xl font-extrabold mt-2">
              {weather.main.temp}Â°C
            </p>
          )}
          {weather.weather && weather.weather[0] && (
            <p className="text-lg mt-2">{weather.weather[0].description}</p>
          )}
        </div>
      </div>
    </>
  );
}

export default App;
