import "./App.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import marker from "./marker.png";
import L from "leaflet";

function App() {
  // Change the map center and marker positions for Pakistan
  const mapCenter = [30.3753, 69.3451]; // Centered on Pakistan
  const markers = [
    {
      location: [31.5497, 74.3436], // Lahore
      title: "Lahore",
    },
    {
      location: [33.6844, 73.0479], // Islamabad
      title: "Islamabad",
    },
    {
      location: [24.8607, 67.0011], // Karachi
      title: "Karachi",
    },
  ];

  const customIcon = L.icon({
    iconUrl: marker,
    iconSize: [40, 40],
    iconAnchor: [16, 32],
  });

  return (
    <>
      <MapContainer zoom={6} center={mapCenter}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright" target="_blank">OpenStreetMap contributors</a>'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {markers.map((marker, index) => (
          <Marker
            key={index}
            position={marker.location}
            icon={customIcon}
          >
            <Popup>{marker.title}</Popup>
          </Marker>
        ))}
      </MapContainer>
    </>
  );
}

export default App;
