import "./App.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import marker from "./marker.png";
import MarkerClusterGroup from "react-leaflet-cluster";
import L from "leaflet";

function App() {
  // Change the map center and marker positions
  const mapCenter = [48.853, 2.349]; // New map center
  const markers = [
    {
      location: [48.853, 2.349], // New marker position
      title: "popUp 1",
    },
    {
      location: [48.854, 2.350], // New marker position
      title: "popUp 2",
    },
    {
      location: [48.855, 2.351], // New marker position
      title: "popUp 3",
    },
  ];

  const customIcon = L.icon({
    iconUrl: marker,
    iconSize: [40, 40],
    iconAnchor: [16, 32],
  });

  return (
    <>
      <MapContainer zoom={13} center={mapCenter}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright" target="_blank">OpenStreetMap contributors</a>'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <MarkerClusterGroup>
          {markers.map((marker, index) => (
            <Marker
              key={index}
              position={marker.location}
              icon={customIcon}
            >
              <Popup>{marker.title}</Popup>
            </Marker>
          ))}
        </MarkerClusterGroup>
      </MapContainer>
    </>
  );
}

export default App;
